mapper.insert:


 select max(COALESCE(NO,1)) as idx FROM notice_board


	insert into notice_board
	(
	NO,title,content,registerDate
	)values
	(
		#{idx}
		 ,#{title}
		, #{content}
		, now()
	)

mapper.update:
update t_board
	set
		update_time = sysdate
		, title = #{title}
		, content = #{content}
		, writer = #{writer}
		, notice_yn = NVL(#{noticeYn}, 'N')
		, secret_yn = NVL(#{secretYn}, 'N')
	WHERE
		idx = #{idx}
